    private void BtnRegisterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnRegisterActionPerformed
        // TODO add your handling code here:
    String Nama, TTL1, TTL2, JK=null, Alamat, RT, RW, Kel, Kec, Kota, Agama, Status, Goldar, Pkrj; //ini adalah pendeklarasian variabel yang akan dipakai dimana bertipe string
    Nama = TfNama.getText();//fungsi untuk mendapatkan inputan yg diisi di TfNama yg akan disimpan sebagai Nama
    TTL1 = TfTmpt.getText();//fungsi untuk mendapatkan inputan yg diisi di TfTmpt yg akan disimpan sebagai TTL1
    TTL2 = TfTl.getText();//fungsi untuk mendapatkan inputan yg diisi di TfTl yg akan disimpan sebagai TTL2
        if (RbLk.isSelected())//pegkondisian dimana jika RbLk yang terpilih
        {JK = RbLk.getText();}//maka RbLk yang akan diambil dan tampil sebagai Jenis Kelamin
        else if (RbPr.isSelected())//tetapi jika RbLk tidak dipilih dan RbPr yang terpilih
        {JK = RbPr.getText();}//maka RbPr yang akan diambil dan tampil sebagai Jenis Kelamin
    Alamat = TfAlamat.getText();//fungsi untuk mendapatkan inputan yg diisi di TfAlamat yg akan disimpan sebagai Alamat
    RT = TfRt.getText();//fungsi untuk mendapatkan inputan yg diisi di TfRt yg akan disimpan sebagai RT
    RW = TfRw.getText();//fungsi untuk mendapatkan inputan yg diisi di TfRw yg akan disimpan sebagai RW
    Kel = TfKel.getText();//fungsi untuk mendapatkan inputan yg diisi di TfKel yg akan disimpan sebagai Kelurahan
    Kec = TfKec.getText();//fungsi untuk mendapatkan inputan yg diisi di TfKec yg akan disimpan sebagai Kecamatan
    Kota = TfKota.getText();//fungsi untuk mendapatkan inputan yg diisi di TfKota yg akan disimpan sebagai Kota
    Agama = CbAgama.getSelectedItem().toString();//pengkondisian dimana Agama yg terpilih dalam CbAgama akan di ambil dan ditampilkan sebagai Agama 
    Status = CbStatus.getSelectedItem().toString();//pengkondisian dimana Status yg terpilih dalam CbStatus akan di ambil dan ditampilkan sebagai Status
    Goldar = CbGoldar.getSelectedItem().toString();//pengkondisian dimana Golongan Darah yg terpilih dalam CbGoldar akan di ambil dan ditampilkan sebagai Golongan Darah
    Pkrj = TfPkrj.getText();//fungsi untuk mendapatkan inputan yg diisi di TfPkrj yg akan disimpan sebagai Pekerjaan
    String Kwrg=null;
        if (RbWna.isSelected())//pegkondisian dimana jika RbWna yang terpilih
        {Kwrg = RbWna.getText();}//maka RbWna yang akan diambil dan tampil sebagai Kewarganegaraan
        else if (RbWni.isSelected())//tetapi jika RbWna tidak dipilih dan RbWni yang terpilih
        {Kwrg = RbWni.getText();}//maka RbWni yang akan diambil dan tampil sebagai Kewarganegaraan
        
    outktp x=new outktp(Nama, TTL1, TTL2, JK, Alamat, RT, RW, Kel, Kec, Kota, Agama, Status, Goldar, Pkrj, Kwrg);//pembuatan objek untuk outktp sebagai outputan dari proses inputan di atas
    x.setVisible(true);//mengatur inputan yang telah diinput bernilai true yang berarti bernilai benar dan akan tampil sebagai outputan
    }//GEN-LAST:event_BtnRegisterActionPerformed

    private void BtnResetActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnResetActionPerformed
        // TODO add your handling code here:
    TfNama.setText("");//mengatur TfNama kosong
    TfTmpt.setText("");//mengatur TfTmpt kosong
    TfTl.setText("");//mengatur TfTl kosong
    buttonGroup1.clearSelection();//menghapus pilihan buttonGroup1 yaitu Jenis Kelamin agar kosong
    TfAlamat.setText("");//mengatur TfAlamat kosong
    TfRt.setText("");//mengatur TfRt kosong
    TfRw.setText("");//mengatur TfRw kosong
    TfKel.setText("");//mengatur TfKel kosong
    TfKec.setText("");//mengatur TfKec kosong
    TfKota.setText("");//mengatur TfKota kosong
    TfPkrj.setText("");//mengatur TfPkrj kosong
    buttonGroup2.clearSelection();//menghapus pilihan buttonGroup2 yaitu Kewarganegaraan agar kosong
    CbAgama.setSelectedItem(null);//mengatur pilihan CbAgama agar bernilai null atau kosong
    CbStatus.setSelectedItem(null);//mengatur pilihan CbStatus agar bernilai null atau kosong
    CbGoldar.setSelectedItem(null);//mengatur pilihan CbGoldar agar bernilai null atau kosong
    }//GEN-LAST:event_BtnResetActionPerformed

